import { Routes, Route, Link, Navigate } from 'react-router-dom';
import rehooz_rectangle from './rehooz-rectangle.png';
import './App.css';

import Home from './pages/Home';
import Profile from './pages/Profile';
import MyListings from './pages/MyListings';
import MyOffers from './pages/MyOffers';
import Browse from './pages/Browse';
import Login from './pages/login';   

function PrivateRoute({ children }) {
  const user = localStorage.getItem("user");
  return user ? children : <Navigate to="/" />;
}

function App() {
  const user = localStorage.getItem("user"); 

  return (
    <div className="App-container">
      <div className="App">
        {/* Header only when logged in */}
        {user && (
          <header className="App-header">
            <div className="header-content">
              <Link to="/home" className="logo-link">
                <img
                  src={rehooz_rectangle}
                  className="rehooz-rectangle"
                  alt="Rehooz logo"
                />
              </Link>

              <nav className="header-nav">
                <Link to="/profile" className="nav-btn">Profile</Link>
                <Link to="/listings" className="nav-btn">My Listings</Link>
                <Link to="/offers" className="nav-btn">My Offers</Link>
                <Link to="/browse" className="nav-btn">Browse</Link>
              </nav>
            </div>
          </header>
        )}

        <Routes>
          {/* Login/Register is the default route */}
          <Route path="/" element={<Login />} />

          {/* Protected routes */}
          <Route path="/home" element={<PrivateRoute><Home /></PrivateRoute>} />
          <Route path="/profile" element={<PrivateRoute><Profile /></PrivateRoute>} />
          <Route path="/listings" element={<PrivateRoute><MyListings /></PrivateRoute>} />
          <Route path="/offers" element={<PrivateRoute><MyOffers /></PrivateRoute>} />
          <Route path="/browse" element={<PrivateRoute><Browse /></PrivateRoute>} />
        </Routes>

        <footer className="App-footer">
          <div className="footer-content">
            <h5>This site was created as part of <strong>CS 4750</strong> at the University of Virginia</h5>
            <p>
              <em>
                Created by Gavin Crigger, Caitlin Fram, Zara Masood, and Karina Yakubisin
              </em>
            </p>
          </div>
        </footer>
      </div>
    </div>
  );
}

export default App;
