// frontend/src/pages/Home.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import rehooz_square from "../rehooz-square.png";

export default function Home() {
  const [user, setUser] = useState(null);

  // Load user from localStorage when component mounts
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);
  }, []);

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.href = "/"; // back to login
  };

  const username = user?.username || "there";

  return (
    <div className="App-body home-page">
      <div className="home-card">
        <div className="home-text">
          <p className="home-subtitle">Welcome back, {username}!</p>
          <h1 className="home-title">ReHooz UVA</h1>
          <p className="home-description">
            Your platform for sustainable selling, buying, and reusing UVA gear.
            List items you no longer need, discover second-hand finds, and keep
            good stuff in circulation.
          </p>

          <div className="home-quick-links">
            <Link to="/browse" className="home-pill primary-pill">
              Browse Listings
            </Link>
            <Link to="/listings" className="home-pill">
              My Listings
            </Link>
            <Link to="/offers" className="home-pill">
              My Offers
            </Link>
          </div>

          {user && (
            <button className="logout-button" onClick={handleLogout}>
              Logout
            </button>
          )}
        </div>

        <div className="home-visual">
          <div className="home-logo-wrapper">
            <img
              src={rehooz_square}
              className="home-logo"
              alt="Rehooz logo"
            />
          </div>
        </div>
      </div>
    </div>
  );
}
