// frontend/src/pages/login.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// Backend base URL (Cloud Run service)
const API_BASE_URL =
  "https://rehooz-app-491933218528.us-east4.run.app/backend";

export default function Login({ setUser }) {
  const navigate = useNavigate();

  const [isRegister, setIsRegister] = useState(false);
  const [form, setForm] = useState({
    username: "",
    email: "",
    city: "",
    password: "",
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const endpoint = isRegister
      ? `${API_BASE_URL}/register.php`
      : `${API_BASE_URL}/login.php`;

    try {
      const res = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      if (!res.ok) {
        const text = await res.text();
        console.error("Backend error response:", text);
        alert("Server error while contacting backend.");
        return;
      }

      const data = await res.json();

      if (data.status === "success") {
        if (isRegister) {
          alert("Account created! You can log in now.");
          setIsRegister(false);
        } else {
          // Save to localStorage AND update App state
          localStorage.setItem("user", JSON.stringify(data.user));
          if (setUser) {
            setUser(data.user);
          }

          alert("Welcome back, " + data.user.username + "!");
          navigate("/home");
        }
      } else {
        alert(data.message || "Error occurred");
      }
    } catch (err) {
      console.error("Network / parse error:", err);
      alert("Network error contacting backend.");
    }
  };

  return (
    <div className="login-page">
      <div
        style={{
          background: "rgba(255,255,255,0.9)",
          borderRadius: "18px",
          padding: "28px 32px 32px",
          boxShadow: "0 10px 26px rgba(15,23,42,0.18)",
          maxWidth: "420px",
          width: "100%",
          textAlign: "center",
        }}
      >
        <h2 style={{ marginBottom: "18px" }}>
          {isRegister ? "Create an Account" : "Login to Rehooz"}
        </h2>

        <form
          onSubmit={handleSubmit}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: "10px",
          }}
        >
          <input
            name="username"
            placeholder="Username"
            value={form.username}
            onChange={handleChange}
            required
          />

          {isRegister && (
            <>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={form.email}
                onChange={handleChange}
                required
              />
              <input
                name="city"
                placeholder="City (optional)"
                value={form.city}
                onChange={handleChange}
              />
            </>
          )}

          <input
            type="password"
            name="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            required
          />

          <button type="submit" style={{ marginTop: "10px", width: "100%" }}>
            {isRegister ? "Sign Up" : "Login"}
          </button>
        </form>

        <p style={{ marginTop: "16px", fontSize: "14px" }}>
          {isRegister ? "Already have an account?" : "Don't have an account?"}{" "}
          <button
            type="button"
            className="link-like"
            onClick={() => setIsRegister(!isRegister)}
          >
            {isRegister ? "Login" : "Register"}
          </button>
        </p>
      </div>
    </div>
  );
}
