import React, { useEffect, useState } from "react";
import rehooz_square from "../rehooz-square.png";

export default function Home() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.href = "/";
  };

  return (
    <div className="home-wrapper">

      {/* LEFT SIDE TEXT */}
      <div className="home-left">
        <h1>Welcome back, {user?.username}!</h1>

        <p className="home-subtext">
          Your platform for sustainable selling, buying, and reusing UVA gear.  
          Keep items in circulation and discover great second-hand finds.
        </p>

        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>

      {/* RIGHT SIDE LOGO CARD */}
      <div className="home-logo-card">
        <img src={rehooz_square} alt="ReHooz Logo" className="home-logo-img" />
      </div>

    </div>
  );
}
